class BaseSistransito:

    def __init__(self):
        self.__url = os.path.join(os.getcwd(),"DEPOSITE_ARQUIVO")

    def __get_file(self):
        list_of_files = os.listdir(self.__url)
        
        while True:
            index = 0
            print("Arquivos disponíveis:")
            for file in list_of_files:
                view = index + 1
                print(f"{view} - {file}")
                index += 1

            print("Defina o arquivo: ")
            print(">", end='')
            selection = int(input())

            print("Deseja confirmar? (S/N)")
            confimation = input().upper()

            if confimation == "S":
                break
        
        selection -= 1

        return list_of_files[selection]

    def get_data(self):
        file = os.path.join(self.__url,self.__get_file())
        df = pd.read_excel(io = file, sheet_name="Planilha1",engine='openpyxl')
        df = df.applymap(lambda s: s.upper() if type (s) == str else s)
        print(df)
        return df

