    def set_new_values(self):
        self.__df["FAIXA DE HORA 2"] = self.__df["FAIXA DE HORA"].apply(lambda faixa: self.__faixa_de_hora[faixa])
        #self.__df["BAIRROS SISP"] = self.__df["BAIRRO OCORRENCIA"].apply(lambda bairro: self.__bairros[bairro] if bairro in self.__bairros.keys() else None)
        self.__df["AISP"] = self.__df["BAIRRO OCORRENCIA"].apply(lambda bairro: self.__aisp[bairro] if bairro in self.__aisp.keys() else None)
        self.__df["REGIAO"] = self.__df["LOCAL OCORRENCIA"].apply(lambda municipio: self.__regiao[municipio] if municipio in self.__regiao.keys() else None)
        #self.__df["LOCAL SISP"] = self.__df["LOCAL OCORRENCIA"].apply(lambda municipio: self.__local_sisp[municipio] if municipio in self.__local_sisp.keys() else None)
        self.__df["RISP"] = self.__df["LOCAL OCORRENCIA"].apply(lambda municipio: self.__risp[municipio] if municipio in self.__risp.keys() else None)
        self.__df["MES FATO SIAC"] = self.__df["MÊS FATO"].apply(lambda mes: self.__mes_fato[mes] if mes in self.__mes_fato.keys() else None)
        self.__df["MES REGISTRO SIAC"] = self.__df["MÊS REGISTRO"].apply(lambda mes: self.__mes_fato[mes] if mes in self.__mes_fato.keys() else None)
        return self.__df

    def set_new_columns(self):
        self.__df["pk"] = self.__df.index
        self.__df["DIA FATO SIAC"] = self.__df["DIA DA SEMANA"].apply(lambda dia: dia[:3])
        self.__df["DATA FATO"] = self.__df["DATA FATO"].apply(lambda x: datetime.datetime.strptime(x,'%d/%m/%Y'))
        self.__df["ANO FATO SIAC"] = self.__df["DATA FATO"].apply(lambda x: x.strftime('%Y'))
        self.__df["MES REGISTRO SIAC"] = self.__df["MES REGISTRO SIAC"].apply(lambda mes: mes[:3])
        self.__df["MARCA"] = self.__df["MARCA/MODELO"].apply(lambda mm: mm.split('/')[0])
        self.__df["MODELO"] = self.__df["MARCA/MODELO"].apply(lambda mm: mm.split('/')[1])
        self.__df["DISTRITO"] = None
        self.__df["TIPO DE VEICULO 1"] = self.__df["TIPO DE VEICULO"]
        self.__df["TIPO DE VEICULO 2"] = None
        self.__df["LOCAL DE OCORRENCIA 2"] = self.__df["LOCAL OCORRENCIA"]
        self.__df["BAIRRO SIAC"] = self.__df["BAIRRO OCORRENCIA"]


        return self.__df

